// frontend/src/App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TicketDisplay from './components/TicketDisplay';
import ConfigurationForm from './components/ConfigurationForm';
import ControlPanel from './components/ControlPanel';

function App() {
  const [ticketCount, setTicketCount] = useState(0);

  useEffect(() => {
    const fetchTicketCount = () => {
      axios.get('http://localhost:5000/tickets/ticket-count')
        .then(res => setTicketCount(res.data.count))
        .catch(console.error);
    };

    fetchTicketCount();
    const interval = setInterval(fetchTicketCount, 1000);  // Poll every second

    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      <h1>Real-Time Ticketing System</h1>
      <ConfigurationForm />
      <ControlPanel />
      <TicketDisplay ticketCount={ticketCount} />
    </div>
  );
}

export default App;
